            anchor: "center",
            color: rgb(255, 127, 255),
            outline: {
                width: 10,
            },
        });
    });
}

function addSceneNavigation() {
    onKeyPress("1", () => go("sprites"));
    onKeyPress("2", () => go("objects"));
    onKeyPress("3", () => go("uvquads"));

    onTouchStart(() => {
        const currentScene = getSceneName();
        const nextScene =
            scenes[(scenes.indexOf(currentScene) + 1) % scenes.length];

        go(nextScene);
    });
}

