    drawLine({
        p1: l.p1,
        p2: l.p2,
        color: WHITE,
    });

    if (clipLineToRect(r, l, res)) {
        drawLine({
            p1: res.p1,
            p2: res.p2,
            color: GREEN,
        });
    }
}

function drawCircleClippedLine(r, l) {
