function addBezier(...objects) {
    const points = [...objects];

    let t = 0;
    return add([
        pos(0, 0),
        {
            draw() {
                const coords = points.map(p => p.pos);
                const c = normalizedCurve(t => evaluateBezier(...coords, t));
                drawCurve(t => evaluateBezier(...coords, t), {
                    segments: 25,
                    width: 4,
                });
