                    p1: points[0].pos,
                    p2: points[1].pos,
                    width: 2,
                    color: rgb(0, 0, 255),
                });
                drawLine({
                    p1: points[3].pos,
                    p2: points[2].pos,
                    width: 2,
                    color: rgb(0, 0, 255),
                });
