};

let curDialog = 0;
let isTalking = false;

// Text bubble
const textbox = add([
    rect(width() - 140, 140, { radius: 4 }),
    anchor("center"),
    pos(center().x, height() - 100),
    outline(4),
]);

// Text
const txt = add([
    text("", {
        size: 32,
        width: width() - 230,
        align: "center",
        styles: {
            "default": {
                color: BLACK,
            },
            "kaplay": (idx, ch) => ({
                color: Color.fromHex("#6bc96c"),
                pos: vec2(0, wave(-4, 4, time() * 6 + idx * 0.5)),
            }),
            "kaboom": (idx, ch) => ({
                color: Color.fromHex("#ff004d"),
                pos: vec2(0, wave(-4, 4, time() * 4 + idx * 0.5)),
                scale: wave(1, 1.2, time() * 3 + idx),
                angle: wave(-9, 9, time() * 3 + idx),
            }),
            // a jump effect
            "surprised": (idx, ch) => ({
                color: Color.fromHex("#8465ec"),
                scale: wave(1, 1.2, time() * 1 + idx),
                pos: vec2(0, wave(0, 4, time() * 10)),
            }),
            "hot": (idx, ch) => ({
                color: Color.fromHex("#ff004d"),
                scale: wave(1, 1.2, time() * 3 + idx),
                angle: wave(-9, 9, time() * 3 + idx),
            }),
        },
        transform: (idx, ch) => {
            return {
                opacity: idx < txt.letterCount ? 1 : 0,
            };
        },
    }),
    pos(textbox.pos),
    anchor("center"),
    {
        letterCount: 0,
    },
]);

// Character avatar
const avatar = add([
    sprite("bean"),
    scale(3),
    anchor("center"),
    pos(center().sub(0, 50)),
]);

onButtonPress("next", () => {
    if (isTalking) return;

    // Cycle through the dialogs
    curDialog = (curDialog + 1) % dialogs.length;
    updateDialog();
});

// Update the on screen sprite & text
function updateDialog() {
    const [char, dialog, eff] = dialogs[curDialog];

    // Use a new sprite component to replace the old one
    avatar.use(sprite(characters[char].sprite));
    // Update the dialog text
    startWriting(dialog, char);

    if (eff) {
        effects[eff]();
    }
}

function startWriting(dialog, char) {
    isTalking = true;
    txt.letterCount = 0;
    txt.text = dialog;

    const writing = loop(0.05, () => {
        txt.letterCount = Math.min(
            txt.letterCount + 1,
            txt.renderedText.length,
        );
        play(characters[char].sound, {
            volume: 0.2,
        });

        if (txt.letterCount === txt.renderedText.length) {
            isTalking = false;
            writing.cancel();
        }
    });
}

// When the game finishes loading, the dialog will start updating
onLoad(() => {
    updateDialog();
});

