	float c = sin(dis * 48.0 + u_time * 8.0 + angle);
	return vec4(c, c, c, 1);
}
`,
);

const t = (n = 1) => time() * n;
const w = (a, b, n) => wave(a, b, t(n));
const px = 160;
const py = 160;
