};

function drawStuff() {
    const mx = (width() - px * 2) / 2;
    const my = (height() - py * 2) / 1;
    const p = (x, y) => vec2(x, y).scale(mx, my).add(px, py);

    drawSprite({
        sprite: "bean",
        pos: p(0, 0),
        angle: t(40),
        anchor: "center",
        scale: w(1, 1.5, 4),
        color: rgb(w(128, 255, 4), w(128, 255, 8), 255),
