        width: w(60, 120, 4),
        height: w(100, 140, 8),
        anchor: "center",
        radius: w(0, 32, 4),
        angle: t(80),
        color: rgb(w(128, 255, 4), 255, w(128, 255, 8)),
        outline,
    });

    drawEllipse({
        pos: p(2, 0),
        radiusX: w(40, 70, 2),
        radiusY: w(40, 70, 4),
