        end: w(180, 360, 1),
        color: rgb(255, w(128, 255, 8), w(128, 255, 4)),
        // gradient: [ Color.RED, Color.BLUE ],
        outline,
    });

    drawPolygon({
        pos: p(0, 1),
        pts: [
            vec2(w(-10, 10, 2), -80),
            vec2(80, w(-10, 10, 4)),
            vec2(w(30, 50, 4), 80),
            vec2(-30, w(50, 70, 2)),
            vec2(w(-50, -70, 4), 0),
        ],
        colors: [
            rgb(w(128, 255, 8), 255, w(128, 255, 4)),
