                uniform: {
                    "u_time": time(),
                    "u_mpos": mousePos().scale(1 / width(), 1 / height()),
                },
            });
        }, drawStuff);
    }
    else {
        drawStuff();
    }
});

// It's a common practice to put all input handling and state updates before rendering.
