// "shake" is taken, so..
function rock() {
    let strength = 0;
    let time = 0;
    return {
        id: "rock",
        require: ["rotate"],
        update() {
            if (strength === 0) {
                return;
            }
            this.angle = Math.sin(time * 10) * strength;
            time += dt();
            strength -= dt() * 30;
            if (strength <= 0) {
                strength = 0;
                time = 0;
            }
        },
        rock(n = 15) {
            strength = n;
        },
    };
}

onKeyPress("space", () => {
    add([
        sprite("egg"),
        pos(player.pos.add(0, 24)),
        rotate(0),
        anchor("bot"),
        rock(),
        "egg",
        { stage: 0 },
    ]);

    player.moveTo(rand(0, width()), rand(0, height()));
});

