layers(["bg", "game", "ui"], "game");

// bg layer
add([
    rect(width(), height()),
    layer("bg"),
    color(rgb(64, 128, 255)),
    // opacity(0.5)
]).add([text("BG")]);

// game layer explicit
add([
    pos(width() / 5, height() / 5),
    rect(width() / 3, height() / 3),
    layer("game"),
    color(rgb(255, 128, 64)),
]).add([text("GAME")]);

// game layer implicit
add([
    pos(3 * width() / 5, 3 * height() / 5),
    rect(width() / 3, height() / 3),
    color(rgb(255, 128, 64)),
]).add([pos(width() / 3, height() / 3), text("GAME"), anchor("botright")]);

// ui layer
add([
    pos(center()),
    rect(width() / 2, height() / 2),
    anchor("center"),
    color(rgb(64, 255, 128)),
]).add([text("UI"), anchor("center")]);

// @ts-check

// Build levels with addLevel()

// Start game
kaplay();

// Load assets
loadSprite("bean", "/sprites/bean.png");
loadSprite("coin", "/sprites/coin.png");
loadSprite("spike", "/sprites/spike.png");
loadSprite("grass", "/sprites/grass.png");
loadSprite("ghosty", "/sprites/ghosty.png");
loadSound("score", "/examples/sounds/score.mp3");

const SPEED = 480;

setGravity(2400);

const level = addLevel([
    // Design the level layout with symbols
    "@  ^ $$",
    "=======",
], {
    // The size of each grid
    tileWidth: 64,
    tileHeight: 64,
    // The position of the top left block
    pos: vec2(100, 200),
    // Define what each symbol means (in components)
    tiles: {
        "@": () => [
            sprite("bean"),
            area(),
            body(),
            anchor("bot"),
            "player",
        ],
        "=": () => [
            sprite("grass"),
            area(),
            body({ isStatic: true }),
            anchor("bot"),
        ],
        "$": () => [
            sprite("coin"),
            area(),
            anchor("bot"),
            "coin",
        ],
        "^": () => [
            sprite("spike"),
            area(),
            anchor("bot"),
            "danger",
        ],
    },
});

// Get the player object from tag
const player = level.get("player")[0];

// Movements
onKeyPress("space", () => {
    if (player.isGrounded()) {
        player.jump();
    }
});

onKeyDown("left", () => {
    player.move(-SPEED, 0);
});

onKeyDown("right", () => {
    player.move(SPEED, 0);
});

// Back to the original position if hit a "danger" item
player.onCollide("danger", () => {
    player.pos = level.tile2Pos(0, 0);
});

// Eat the coin!
player.onCollide("coin", (coin) => {
    destroy(coin);
    play("score");
});

