    rgb(0, 255, 0),
    rgb(43, 71, 252),
    rgb(255, 255, 0),
    rgb(255, 0, 255),
];

let playerCount = 0;

function addPlayer(gamepad) {
    const player = add([
        pos(center()),
        anchor("center"),
        sprite("bean"),
        color(playerColors[playerCount]),
        area(),
        body(),
        doubleJump(),
    ]);

    playerCount++;

    onUpdate(() => {
        const leftStick = gamepad.getStick("left");

        if (gamepad.isPressed("south")) {
            player.doubleJump();
        }

        if (leftStick.x !== 0) {
            player.move(leftStick.x * 400, 0);
        }
    });
}

