// custom comp
function handleout() {
    return {
        id: "handleout",
        require: ["pos"],
        update() {
            const spos = this.screenPos();
            if (
                spos.x < 0
                || spos.x > width()
                || spos.y < 0
                || spos.y > height()
            ) {
                // triggers a custom event when out
                this.trigger("out");
            }
        },
    };
}

const SPEED = 640;

function shoot() {
    const center = vec2(width() / 2, height() / 2);
    const mpos = mousePos();
    add([
        pos(center),
        sprite("bean"),
        anchor("center"),
        handleout(),
        "bean",
        { dir: mpos.sub(center).unit() },
    ]);
}

