function getGlobalShape(s) {
    const t = s.transform;
    return s.getShape().transform(t);
}

onUpdate(() => {
    const shapes = get("shape");
    shapes.forEach(s1 => {
        if (
            shapes.some(s2 =>
                s1 !== s2 && getGlobalShape(s1).collides(getGlobalShape(s2))
            )
        ) {
            s1.color = RED;
        }
        else {
            s1.color = BLUE;
        }
    });
});

onDraw(() => {
    const shapes = get("shape");
    shapes.forEach(s => {
        const shape = getGlobalShape(s);
        // console.log(tshape)
        switch (shape.constructor.name) {
            case "Ellipse":
                pushTransform();
                pushTranslate(shape.center);
                pushRotate(shape.angle);
                drawEllipse({
                    pos: vec2(),
                    radiusX: shape.radiusX,
                    radiusY: shape.radiusY,
                    fill: false,
                    outline: {
                        width: 4,
                        color: rgb(255, 255, 0),
                    },
                });
                popTransform();
                break;
            case "Polygon":
                drawPolygon({
                    pts: shape.pts,
                    fill: false,
                    outline: {
                        width: 4,
                        color: rgb(255, 255, 0),
                    },
                });
                break;
        }
    });
});

onMousePress(() => {
    const shapes = get("shape");
    const pos = mousePos();
    const pickList = shapes.filter((shape) =>
        getGlobalShape(shape).contains(pos)
    );
    selection = pickList[pickList.length - 1];
    if (selection) {
        get("selected").forEach(s => s.unuse("selected"));
        selection.use("selected");
    }
});

onMouseMove((pos, delta) => {
    get("selected").forEach(sel => {
        sel.moveBy(delta);
    });
    get("turn").forEach(laser => {
        const oldVec = mousePos().sub(delta).sub(laser.pos);
        const newVec = mousePos().sub(laser.pos);
        laser.angle += oldVec.angleBetween(newVec);
    });
});

onMouseRelease(() => {
    get("selected").forEach(s => s.unuse("selected"));
    get("turn").forEach(s => s.unuse("turn"));
});

